import React from 'react'
import { useEffect, useState } from 'react'
import Create from './Create'
import axios from 'axios'
import {
  BsFillTrashFill,
  BsCircleFill,
  BsCheckCircleFill,
} from "react-icons/bs";


function Home() {
    const [todos, setTodos] = useState([])
    useEffect(() => {
      axios.get('http://localhost:3001/get')
      .then(result => setTodos(result.data))
      .catch(err => console.log(err))
    })
  return (
    <div className='home'>
        <h1>To Do List</h1>
        < Create />
        {todos.length === 0 ? 
        (<h2 className='noRecord'>No Record</h2>) : (
        <div className='task'>
          <div>
            <BsCircleFill/>
           { todos.map(todo => (
                todo.task
            ))}
          </div>
          <div>
            <span><BsFillTrashFill/></span>
          </div>
        </div>)
}
        
    </div>
  )
}

export default Home
